import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import Header from './components/Header';
import ExploreGallery from './components/ExploreGallery';
import MintForm from './components/MintForm';
import UserDashboard from './components/UserDashboard';

function DigitalRain() {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    let width = window.innerWidth;
    let height = window.innerHeight;
    canvas.width = width;
    canvas.height = height;

    const columns = Math.floor(width / 16);
    const drops = Array(columns).fill(1);
    const chars = 'あアｱｳﾍɸ♥♪★mehrnosh<3adnan＊％&$0123456789';

    function draw() {
      ctx.fillStyle = 'rgba(5,6,10,0.15)';
      ctx.fillRect(0, 0, width, height);

      ctx.fillStyle = '#0F0';
      ctx.font = '16px monospace';

      for (let i = 0; i < drops.length; i++) {
        const text = chars[Math.floor(Math.random() * chars.length)];
        ctx.fillText(text, i * 16, drops[i] * 16);

        if (drops[i] * 16 > height && Math.random() > 0.95) {
          drops[i] = 0;
        }
        drops[i]++;
      }

      requestAnimationFrame(draw);
    }

    draw();

    function handleResize() {
      width = window.innerWidth;
      height = window.innerHeight;
      canvas.width = width;
      canvas.height = height;
    }

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  return <canvas ref={canvasRef} style={{ position: 'fixed', inset: 0, zIndex: 0, pointerEvents: 'none' }} />;
}

function App() {
  return (
    <div className="App">
      <Header />
      <DigitalRain />
      <header className="App-header">
        <h1>Welcome to NFT Gallery</h1>
        <p>Connect your wallet to view and mint NFTs.</p>
      </header>
    </div>
  );
}

export default App;
