import React, { useState, useEffect } from 'react';
import './ExploreGallery.css';

function ExploreGallery() {
  const [pets, setPets] = useState([]);
  const [filteredPets, setFilteredPets] = useState([]);
  const [filters, setFilters] = useState({
    species: 'all',
    priceRange: 'all',
    insurance: 'all',
    printing: 'all'
  });
  const [selectedPet, setSelectedPet] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadPets();
  }, []);

  useEffect(() => {
    applyFilters();
  }, [filters, pets]);

  const loadPets = async () => {
    setLoading(true);
    // Mock data - replace with actual contract call
    const mockPets = [
      {
        id: 1,
        name: 'Golden Max',
        species: 'Golden Retriever',
        price: '0.5',
        image: '',
        nfcId: 'NFC-001',
        insurance: true,
        printing: true,
        owner: '0x1234...5678',
        traits: { health: 'Excellent', age: '2 years' }
      },
      {
        id: 2,
        name: 'Luna Cat',
        species: 'Persian Cat',
        price: '0.3',
        image: '',
        nfcId: 'NFC-002',
        insurance: true,
        printing: false,
        owner: '0xabcd...efgh',
        traits: { health: 'Good', age: '1 year' }
      },
      {
        id: 3,
        name: 'Buddy Bear',
        species: 'Teddy Bear',
        price: '0.8',
        image: '',
        nfcId: 'NFC-003',
        insurance: false,
        printing: true,
        owner: '0x9876...5432',
        traits: { health: 'Excellent', age: '3 years' }
      }
    ];

    setTimeout(() => {
      setPets(mockPets);
      setFilteredPets(mockPets);
      setLoading(false);
    }, 1000);
  };

  const applyFilters = () => {
    let filtered = [...pets];

    if (filters.species !== 'all') {
      filtered = filtered.filter(pet => pet.species === filters.species);
    }

    if (filters.priceRange !== 'all') {
      const [min, max] = filters.priceRange.split('-').map(Number);
      filtered = filtered.filter(pet => {
        const price = parseFloat(pet.price);
        return price >= min && price <= max;
      });
    }

    if (filters.insurance !== 'all') {
      filtered = filtered.filter(pet => pet.insurance === (filters.insurance === 'true'));
    }

